$( ".burger" ).on( "click", function() {
	if ( $( '.menu' ).hasClass( "active" ) ) {
		$( '.menu' ).removeClass( 'active' );
	} else {
		$( '.menu' ).addClass( 'active' );
	}
});

$( ".menu li a" ).on( "click", function(e) {
	$('.' + $(this).data("goto"))[0].scrollIntoView( true );
	window.scrollBy(0,-64);
	e.preventDefault();
});